/***********************
Latest Project filter
**********************/

$(".filter-button").click(function(e){
    e.preventDefault();

    var value = $(this).attr('data-filter');

    if(value == "all")
    {
        //$('.filter').removeClass('hidden');
        $('.filter').show('1000');
    }
    else
    {
        //$('.filter[filter-item="'+value+'"]').removeClass('hidden');
        //$(".filter").not('.filter[filter-item="'+value+'"]').addClass('hidden');
        $(".filter").not('.'+value).hide('3000');
        $('.filter').filter('.'+value).show('3000');

    }
});

if ($(".filter-button").removeClass("active")) {
    $(this).removeClass("active");
}
$(this).addClass("active");



/*****************
Flickr feed plugin
******************/

$.getJSON("http://api.flickr.com/services/feeds/photos_public.gne?id=129367021@N05&format=json&jsoncallback=?", function(data) {
        var target = "#latest-flickr-images #flickr_grid"; // Where is it going?
        for (i = 0; i <= 9; i = i + 1) { // Loop through the 10 most recent, [0-9]
               var pic = data.items[i];
               var liNumber = i + 1; // Add class to each LI (1-10)
               $(target).append("<a class='flickr-image no-" + liNumber + "' title='" + pic.title + "' href='" + pic.link + "'><img src='" + pic.media.m + "' /></a>");
        }
});


/*********************************
  Type effect JS https://css-tricks.com/snippets/css/typewriter-effect 
*********************************/

var TxtType = function(el, toRotate, period) {
    this.toRotate = toRotate;
    this.el = el;
    this.loopNum = 0;
    this.period = parseInt(period, 10) || 2000;
    this.txt = '';
    this.tick();
    this.isDeleting = false;
};

TxtType.prototype.tick = function() {
    var i = this.loopNum % this.toRotate.length;
    var fullTxt = this.toRotate[i];

    if (this.isDeleting) {
    this.txt = fullTxt.substring(0, this.txt.length - 1);
    } else {
    this.txt = fullTxt.substring(0, this.txt.length + 1);
    }

    this.el.innerHTML = '<span class="wrap">'+this.txt+'</span>';

    var that = this;
    var delta = 200 - Math.random() * 100;

    if (this.isDeleting) { delta /= 2; }

    if (!this.isDeleting && this.txt === fullTxt) {
    delta = this.period;
    this.isDeleting = true;
    } else if (this.isDeleting && this.txt === '') {
    this.isDeleting = false;
    this.loopNum++;
    delta = 500;
    }

    setTimeout(function() {
    that.tick();
    }, delta);
};

window.onload = function() {
    var elements = document.getElementsByClassName('typewrite');
    for (var i=0; i<elements.length; i++) {
        var toRotate = elements[i].getAttribute('data-type');
        var period = elements[i].getAttribute('data-period');
        if (toRotate) {
          new TxtType(elements[i], JSON.parse(toRotate), period);
        }
    }
    // INJECT CSS
    var css = document.createElement("style");
    css.type = "text/css";
    css.innerHTML = ".typewrite > .wrap { border-right: 2px solid #fff}";
    document.body.appendChild(css);
};


